mapper.insert:

	select seq_board.nextval from dual

insert into tbl_board(
	bno
	,title 
	,content
	,writer 
	,ipaddress)
values(
	#{bno}
	,#{title}
	,#{content}
	,#{writer}
	,SYS_CONTEXT ('USERENV', 'IP_ADDRESS'))

mapper.update:
update tbl_board
set title = #{title}
,content = #{content}
,writer = #{writer}
,updateDate = sysdate
where bno = #{bno} and deleteYn = 'N'

mapper.delete:
update tbl_board
set deleteYn = 'Y'
where bno = #{bno}

mapper.update:
UPDATE tbl_board SET
HIT = HIT+1
WHERE BNO = #{bno}
